function applyTheme(e) {
  var t = document.documentElement,
    a = document.getElementById("theme-icon");
  "dark" === e
    ? (t.setAttribute("data-bs-theme", "dark"),
      a.classList.remove("fa-sun"),
      a.classList.add("fa-moon"),
      document.body.classList.remove("light-theme"),
      document.body.classList.add("dark-theme"))
    : (t.setAttribute("data-bs-theme", "light"),
      a.classList.remove("fa-moon"),
      a.classList.add("fa-sun"),
      document.body.classList.remove("dark-theme"),
      document.body.classList.add("light-theme"));
}
document.addEventListener("DOMContentLoaded", function () {
  var e = localStorage.getItem("theme") || "light";
  applyTheme(e);
}),
  document
    .getElementById("theme-toggle")
    .addEventListener("click", function () {
      var e = document.documentElement.getAttribute("data-bs-theme"),
        t = "light" === e ? "dark" : "light";
      applyTheme(t), localStorage.setItem("theme", t);
    });
